function setup()
   createWindow(200, 200);

   background('purple');
   text('Press any key', 10, 40);
end

-- no draw
function draw() return end 

function keyPressed(key, keyCode)
   print(key, keyCode);

   background('purple');

   if key ~= nil then 
       text('Pressed: ' .. key, 10, 40);
   end
end
