function setup()
   createWindow(200, 200);

   background('purple');
   text('Release any key', 10, 40);
end

-- no draw
function draw() return end 

function keyReleased(key, keyCode)
   print(key, keyCode);

   background('purple');

   if key ~= nil then 
       text('Released: ' .. key, 10, 40);
   end
end
