function mouseReleased()
  size = 64;
end

size = 0;

function setup()
  createWindow(400, 400);
end

function draw()
  background(51);

  circle(mouseX, mouseY, size);
end

-- Change circle size when mouse is scrolled
function mouseWheel(delta)
  size = size + delta;
end
