size = 12;

function setup()
  createWindow(400, 400);
  textAlign(CENTER);
end

function draw()
  background('purple');

  text('Click to grow the circle', width/2, 50);
  circle(width/2, height/2, size);
end

-- Increase circle size when mouse pressed
function mousePressed()
  size = size + 3;
end
