function setup()
    createWindow(400, 400);
end

function draw()
    background('purple');

    push();
    fill(155, 255, 0);
    strokeWeight(12);

    -- Affected by style
    circle(300, 200, 32);
    pop();

    -- Not Affected by style
    circle(100, 200, 32);
end

function setup()
    createWindow(400, 400);
end

function draw()
    background('purple');

    push();
    fill('red');
    translate(200, 200);

    -- Affected by transformation
    circle(0, 0, 32);
    pop();

    -- Not Affected by transformation
    circle(0, 0, 32);
end
