x = 0;
r = 16;
speed = 50;

function setup()
  createWindow(200, 200);

  frameRate(24); -- Set the frame rate
end

function draw()
  background('purple');
  text('fps: ' .. frameRate(), 20, 30);

  square(x, 100, r);
  if x > width+r then 
    x = -r;
  else
    x = x + speed * deltaTime;
  end
end

