x = 0;

function setup()
  createWindow(400, 400);
  frameRate(24);
end

function draw()
  background(51);
  text('fps: ' .. frameRate(), 20, 10);

  circle(x, 200, 32);
  x = x + 1;
end

x = 0;
vx = 128;

function setup()
  createWindow(400, 400);
  frameRate(24); -- try with 60
end

function draw()
  background(51);

  circle(x, height/2, 32);

  -- Get the same velocity with different framerates
  x = x + vx * deltaTime;
end

